//perform correlation betweent the values of HealthRate and the Urban Population
import org.apache.spark.mllib.linalg._
import org.apache.spark.mllib.stat.Statistics
import org.apache.spark.mllib.stat.MultivariateStatisticalSummary

val urbanValues = urbanPop.map(row => {                         //get the values corresponding to the urban population
        val rowEle = row.getAs[String]("value")
        if(rowEle != null){
            rowEle.toDouble    
        }
        else 0.0
    })

val healthValues = healthExp.map(row => {                       //get the values corresponding to the health expenditures
        val rowEle = row.getAs[String]("value")
        if(rowEle != null){
            rowEle.toDouble
        }
        else 0.0
    })

val huCorr = Statistics.corr(healthValues, urbanValues, "pearson")  //compute the correlation between the values
//a very high positive correlation 

//list the regions that have above mean health expenditures
val healthVec = healthValues.map( ele => Vectors.dense(ele))

val healthSumm = Statistics.colStats(healthVec)                     //compute the health statistics summary

println("The statistics of the health expenditure data is given below : ")
println("The mean of the health data is : " + healthSumm.mean)
println("The variance of the data is : " + healthSumm.variance)
println("The maximum value of the health data : " + healthSumm.max)
println("The minimum value of the health data : " + healthSumm.min)

//get the regions that have their health spending above the average
val aboveMean = healthExp.filter(healthExp("value") > healthSumm.mean.apply(0)).select("country", "value", "date")
aboveMean.registerTempTable("AboveMean")

