//define a general function to transform the input dataframe into a usable one by manually exploding the rows
import com.google.gson.Gson
import org.apache.spark.sql.DataFrame
import scala.collection.mutable.ArraySeq

case class Observ(OBS_VALUE: String, TIME_PERIOD: String)                       //schema for a single observation value
case class DataPoints(REF_AREA: Array[String], Obs: Array[Array[Observ]])       //schema for a single datapoint in the input dataframe
case class Row(country: String, value: String, year: String)                    //schema for a single row in the output dataframe

def explode(inputFrame: DataFrame): DataFrame = {

    val values = inputFrame.select("CompactData.DataSet.Series.REF_AREA", "CompactData.DataSet.Series.Obs")    //get the values : country, value, year
    val getValues = values.toJSON.collect()

    val gson = new Gson                                                         //get the gson object used for parsing the json
    val data = gson.fromJson(getValues(0), classOf[DataPoints])                 //parse the json to get the data

    var newSeq = new ArraySeq[Row](0)                                           //the arraySeq used to store the exploded rows

    for(i <- 0 until data.REF_AREA.length){                                     //loop through the entire set of values
        for(j <- 0 until data.Obs(i).length){
            newSeq = newSeq.:+(Row(data.REF_AREA(i), data.Obs(i)(j).OBS_VALUE, data.Obs(i)(j).TIME_PERIOD))
        }
    }
    return sc.parallelize(newSeq).toDF()
}

//define a generic fucntion to get the data corresponding to a specific country and dataset

def getCountryData(country: String, inputFrame: DataFrame) : String = {                         //define a generic function to get the values in required form

    val filteredData = inputFrame.filter($"country".like(country)).select("year", "value")
    val countryJson = filteredData.toJSON.collect().mkString(",")
    val countryData = new StringBuilder(countryJson).replaceAllLiterally("{","[").replaceAllLiterally("}", "]").replaceAllLiterally("\"", "")
                                .replaceAllLiterally("year:", "").replaceAllLiterally("value:", " ")
    return countryData
}

