//this paragraph defines the case classes used to form the schemas for the datasets and some utility functions used in later paragraphs

case class CommonType1(country : String, values : Array[String])                  //define a common type1 for the different types of datasets
case class CommonType2(country : String, typ : String , values : Array[String])   //define a common type2 for the different types of datasets

//define generic case classes to form a generic schema containing only the relevant fields from the datasets
case class Column17(typ : String, country : String, y14 : String, y13 : String, y12 : String, y11 : String, y10 : String, y9 : String,
                    y8 : String, y7 : String, y6 : String, y5 : String, y4 : String, y3 : String, y2 : String, y1 : String, y0 : String){

                        def getValues() : Array[String] = {
                            return Array(y0, y1, y2, y3, y4, y5, y6, y7, y8, y9, y10, y11, y12, y13, y14)
                        }
                    }

//utility function to check the values array returned by the case classes and remove inconsistent values
def getGoodValues(valueArr : Array[String]) : Array[String] = {

    valueArr(0) = if((valueArr(0).equals(": z")) || (valueArr(0).equals(": "))) "0" else valueArr(0)    //set the first value only 

    for(i <- 1 until valueArr.length){                               //iterate over the rest of the array to remove inconsistent values

        if(valueArr(i).equals(": z")){                               //the symbol for no data for the country in this(current) dataset
            valueArr(i) = "0";
        }
        else if(valueArr(i).equals(": ")){
            valueArr(i) = valueArr(i - 1)
        }
    }
    return valueArr                                                  //return the Array of Good formed values
}

//define a function to get the value for a particular year and a dataset for a specific category 
def getYearValue(inputSet : DataSet[Column17], country : String, typ : String, year : Int, startYear : Int) : String = {

    val filterData = inputSet.filter(ele => (ele.country.equals(country) && ele.typ.equals(typ)))       //get the filtered data for a particular country and type
    val dataArr    = filterData.collect.toArray                                                         //get the array for the values
    val valueArr   = dataArr(0).getValues
    var index      = year - startYear                        //the index into the array for the year passed as parameter
    var yearVal    = valueArr(index)                         //get the initial value for the year passed as parameter

    while(yearVal.equals(": ") && index > 0){                //check for the values in the previous years if values is not found in the present year

        index -= 1;
        yearVal = valueArr(index)
    }

    if(index == 0 && valueArr(index).equals(": ")){          //if reached the last year without finding any value
            return "0"
    }

    return yearVal
}

//define a generic function to get the values in the table display format - Row(country, year, value)
def getTablDispData1(inputSet : DataSet[CommonType1], startYear : Int) : String = {

    val dataArr   = inputSet.collect.toArray                                      //collect the values as an array

    var resultArr = Array("country\tyear\tvalue")                                 //form the header for the display format

    for(i <- 0 until dataArr.length){
        var year = startYear
        var yearValues = getGoodValues(dataArr(i).values)                         //get the array of values for the current country

        for(j <- 0 until yearValues.length){
            resultArr = resultArr.:+(dataArr(i).country + "\t" + year + "\t" + yearValues(j))
            year += 1
        }
    }

    return resultArr.mkString("\n")                                               //form the table display by inserting '\n' character
}

//define a generic function to get the values for a particular type of a dataset in the table display format
def getTablDispData2(inputSet : DataSet[CommonType2], typ : String, startYear : Int) : String = {

    val dataArr = inputSet.collect.toArray                                        //collect the values as an array

    var resultArr = Array("country\tyear\tvalue\t" + typ)                         //form the header for the display format

    for(i <- 0 until dataArr.length){
        var year = startYear
        var yearValues = getGoodValues(dataArr(i).values)                         //get the array of values for the current country

        for(j <- 0 until yearValues.length){
            resultArr = resultArr.:+(dataArr(i).country + "\t" + year + "\t" + yearValues(j) + "\t" + dataArr(i).typ) 
            year += 1
        }
    }

    return resultArr.mkString("\n")                                             //form the table display by inserting '\n' character
}

//define a generic function to get the values in the comparison format for table display of two datasets
def getCmpTablDisp(inputXSet : DataSet[CommonType1], inputYSet : DataSet[CommonType1], startXYear : Int, startYYear : Int) : String = {

    val dataXArr = inputXSet.collect.toArray                                    //collect the values as an array
    val dataYArr = inputYSet.collect.toArray

    var resultArr = Array("country\txValue\tyValue")                            //form the header for the display format
    val outLength = Math.min(dataXArr.length, dataYArr.length)
    val xStIndex  = if(startYYear > startXYear) startYYear - startXYear else 0  //starting index for the x values by the year
    val yStIndex  = if(startXYear > startYYear) startXYear - startYYear else 0  //starting index for the y values by the year
    val inLength  = Math.min(dataXArr(0).values.length - xStIndex, dataYArr(0).values.length - yStIndex)

    for(i <- 0 until outLength){
        var yearXValues = getGoodValues(dataXArr(i).values)                     //get the values for the current country
        var yearYValues = getGoodValues(dataYArr(i).values)
        var xIndex = xStIndex
        var yIndex = yStIndex

        for(j <- 0 until inLength){
                resultArr = resultArr.:+(dataXArr(i).country + "\t" + yearXValues(xIndex) + "\t" + yearYValues(yIndex))
                xIndex += 1
                yIndex += 1
        }
    }

    return resultArr.mkString("\n")                                             //form the table display by inserting '\n' character
}

//function to generate the values for a specific country and a specific type from a particular function 
def getCountryData(inputSet : DataSet[Column17], country : String, typ : String , startYear : Int, endYear : Int) : String = {

    val filteredData  = inputSet.filter(ele => (ele.typ.equals(typ) && ele.country.equals(country)))     //get the data for a country and a type from input DataSet
    val filDataArr    = filteredData.collect.toArray                                                     //get the array for the data values
    val valueArr      = filDataArr(0).getValues
    val values        = getGoodValues(valueArr)                       //get the values after filtering the data and removing inconsistent values
    var countryArr    = new Array[String](0)                          //the output array to contain the values
    var year = startYear

    while(year <= endYear){
        countryArr = countryArr.:+("{x:" + year + ", y:" + values(year - startYear) + "}")
        year += 1;
    }
    return countryArr.mkString(",")
}

//function to get the well formed json string for the visualizations
def getJson(inputSet : DataSet[Column17], typ : String) : String = {

    val geVal  = getCountryData(inputSet, "DE", typ, 2000, 2014)    //get the data for Germany
    val ukVal  = getCountryData(inputSet, "UK", typ, 2000, 2014)    //get the data for United Kingdom
    val frVal  = getCountryData(inputSet, "FR", typ, 2000, 2014)    //get the data for France
    val spVal  = getCountryData(inputSet, "ES", typ, 2000, 2014)    //get the data for Spain
    val itVal  = getCountryData(inputSet, "IT", typ, 2000, 2014)    //get the data for Italy

    //got the values for all the representative nations, now form the json
    val data = s"""
    [
        {
            values: [$geVal],
            key: 'Germany',
            color: '#ff8221'
        },
        {
            values: [$ukVal],
            key: 'United Kingdom',
            color: '#ab2341'
        },
        {
            values: [$frVal],
            key: 'France',
            color: '#673ef2'
        },
        {
            values: [$spVal],
            key: 'Spain',
            color: '#451afb'
        },
        {
            values: [$itVal],
            key: 'Italy',
            color: '#12fa34'
        }
    ]
    """

    return data                     //return the json string
}

//utility function to form the json string for the various countries(specific to the bullet chart)
def getBulletJson(title : String, subTitle : String, val1 : String, val2 : String, color : String) : String = {

    //simply form the json and return it
    val bulletJson = s"""
    {
        "title": "$title",
        "subtitle": "$subTitle",
        "ranges": [$val1, $val2],
        "color": "$color",
        "measures": [$val2],
        "markers": [$val2]

    }
    """
    return bulletJson
}

