//sankey representation for outlinks from 'fangraphs.com'

println(s"""%html
<html>
  <head>
    <script type="text/javascript" src="https://www.gstatic.com/charts/loader.js"></script>

    <script type="text/javascript">
      google.charts.load('current', {'packages':['sankey']});
      google.charts.setOnLoadCallback(drawChart);

      function drawChart() {

        var data = new google.visualization.DataTable();
        data.addColumn('string', 'From');
        data.addColumn('string', 'To');
        data.addColumn('number', 'Weight');
        data.addRows($mayBegLnkWt1);

        var colors = ['#a6cee3', '#b2df8a', '#fb9a99', '#fdbf6f',
                  '#cab2d6', '#ffff99', '#1f78b4', '#33a02c'];

        var options = {
          height: 600,
          sankey: {
            node: {
              colors: colors
            },
            link: {
              colorMode: 'gradient',
              colors: colors
            }
          }
        };

        // Instantiates and draws our chart, passing in some options.
        var chart = new google.visualization.Sankey(document.getElementById('sankey1'));
        chart.draw(data, options);
      }
    </script>
  </head>

  <div id="sankey1" style="width: 500px; height: 600px;"></div>
</html>
""")

