//start proposed analysis on a testing basis - first get the circles that have maximum number of friends/users

def compare(circle1 : (String, Int), circle2 : (String, Int)) : Boolean = {         //function to compare the values of circle lengths
    if(circle1._2 >= circle2._2)
        return true
    else return false
}

//get the most prominent circles by getting the largest circles by size
val circles = rawCicle.map(ele => ele.split("\t")).map(s => (s(0), s.drop(1)))      //get the circles as labels and values of nodes in that circle
val circLen = circles.map(ele => (ele._1, ele._2.length))
val circLenArr = circLen.collect()                                                  //get the circles lengths in array form to sort in descending order
circLenArr.sortWith((ele1, ele2) => compare(ele1, ele2))                            //sorted in descending order to get the largest circle

//get the users that comprise the circle 15 and circle 16 that are most prominent - largest in size
val circle15 = circles.filter(ele => ele._1.equals("circle15"))
val circle16 = circles.filter(ele => ele._1.equals("circle16"))

//filter the feature names RDD to get the features in readable form
val features = featName.map(ele => ele.split(";anonymized")(0)).map(ele => ele.split(" ")).map(s => (s(0).toLong, s(1)))       //get the features from the file
val nodeFeats = rawFeatr.map(ele => ele.split(" ")).map(s => (s(0), s.drop(1)))              //get the features for the rest of the nodes

val circl15Arr = circle15.collect()                 //get the circle 15 elements as an array
val circ15Feat = nodeFeats.filter(ele => circl15Arr(0)._2.contains(ele._1))                  //the users in circle15 and their features

val circl16Arr = circle16.collect()                 //get the circle 16 elements as an array
val circ16Feat = nodeFeats.filter(ele => circl16Arr(0)._2.contains(ele._1))                  //the users in circle16 and their features

val featArr = features.map(ele => ele._2).collect()                                 //get the array corresponding to the feature strings

//get the features for the users of the prominent circles

val circ15FeatName = circ15Feat.map(ele => {                                        //get the feature labels that are present for users in circle 15
    var featNmArr = Array[String]()
    for(i <- 0 until ele._2.length){
        if(ele._2(i) == "1")
            featNmArr = featNmArr.:+(featArr(i))                                    //from the feature labels array extract those that are present for this user
    }
    (ele._1, featNmArr)
})

val circ16FeatName = circ16Feat.map(ele => {                                        //get the feature labels that are present for users in circle 16
    var featNmArr = Array[String]()
    for(i <- 0 until ele._2.length){
        if(ele._2(i) == "1")
            featNmArr = featNmArr.:+(featArr(i))                                    //from the feature labels array extract those that are present for this user
    }
    (ele._1, featNmArr)
})

val egoFeat = rawEFeat.flatMap(ele => ele.split(" "))                               //get the features of the ego user (only the indexes) of the valid features
                      .map(ele => ele.toInt)
                      .zipWithIndex()
                      .map( _.swap)
                      .filter(ele => (ele._2 == 1))

val egoFeatNm = egoFeat.map(ele => featArr(ele._1.toInt))                           //the features retrieved in the String form for the ego user

circ15FeatName.toDF().registerTempTable("User15")                                   //get the dataframe and the table for the most prominent circle
circ16FeatName.toDF().registerTempTable("User16")                                   //get the dataframe and the table for the second most prominent circle

println("%html <h4>The feature labels of the ego user are :- </h4>")                //print the features of the ego user
egoFeatNm.take(30).foreach(println)

//this paragraph works on the second feature for the Social Networks - analysing users that are in multiple circles

val allUsers = rawCicle.flatMap(ele => ele.split("\t"))                       //get the users in the string form 
val usrCount = allUsers.map(ele => (ele, 1)).reduceByKey((x, y) => x + y)     //count all the users to get the duplicates 

val dupUsers = usrCount.filter(ele => (ele._2 != 1)).map(ele => ele._1)       //get the duplicate users
val dupUsrArr = dupUsers.collect()                                            //the array of duplicate users
val dupUsrFeat = nodeFeats.filter(ele => dupUsrArr.contains(ele._1))          //get the feature strings of the duplicate users
val dupUsrFeatName = dupUsrFeat.map(ele => {
    var featNmArr = Array[String]()
    for(i <- 0 until ele._2.length){
        if(ele._2(i) == "1")
            featNmArr = featNmArr.:+(featArr(i))
    }
    (ele._1, featNmArr)
})

dupUsrFeatName.toDF().registerTempTable("DupUsers")

//this paragraph finds the strength of the circles by finding the mutual relationships within the circles

val edgeRdd = rawEdges.map(ele => ele.split(" ")).map(ele => (ele(0), ele(1)))      //convert the input edges to tuples of edges(source, destination)
val edgeMapRdd = edgeRdd.groupByKey()                                               //get the map for the edges using just groupByKey()
val edgeMap = edgeMapRdd.map(ele => (ele._1, ele._2.toArray)).collectAsMap()


//iterate through the edges in the circles and calculate the connection percentages for each of them
val circStrength = circles.map(ele => {

    var trueConnec = 0                                      //counter to count the connections that actually exist within the circles

    for(i <- 0 until ele._2.length - 1){                    //check all the nodes in the circles for the connections in the network graph

        val valSeq = edgeMap.getOrElse(ele._2(i), null)                             //get the nodes that are connected to current node

        if(valSeq != null){
            for(j <- (i+1) until ele._2.length){
                if(valSeq.contains(ele._2(j))){
                    trueConnec += 1                         //the connection between these two nodes exist so increment the trueConnec counter
                }
            }
        }
    }
    (ele._1, trueConnec)
})

val ptveStrgth = circStrength.filter(ele => (ele._2 != 0))
ptveStrgth.toDF().registerTempTable("Strengths")

//this paragraph generates the data needed for the visualization of the circles in the social network
import scala.collection.mutable.ArraySeq

val circlesArr = circles.collect()                      //get the array for the circles
var linksArr = ArraySeq[String]()                       //the array to hold the links in the circles
var nodesArr = ArraySeq[String]()                       //the array to hold the nodes in the graph

for(i <- 10 until 17){
    for(j <- 0 until circlesArr(i)._2.length){

        nodesArr = nodesArr.:+("{ \"id\" : \"" + circlesArr(i)._2(j) + "\", \"group\" : " + circlesArr(i)._1.substring(6) + "}")        //create the nodes string
        val valSeq = edgeMap.getOrElse(circlesArr(i)._2(j), null)

        if(valSeq != null){
            for(k <- (j+1) until circlesArr(i)._2.length){
                if(valSeq.contains(circlesArr(i)._2(k))){
                    linksArr = linksArr.:+("{ \"source\" : \"" + circlesArr(i)._2(j) + "\", \"target\" : \"" + circlesArr(i)._2(k) + "\", \"value\" : 1 }"  )
                }
            }
        }
    }
}

val nodes = nodesArr.mkString(",")                      //collect all the nodes
val links = linksArr.mkString(",")                      //collect all the links

val circleGraph = s"""
{
    "nodes" : [$nodes],
    "links" : [$links]
}
"""

//this paragraph generates the visualization for the circles in the social network
println("""%html
<style>
    #circlegraph{
        width: 960;
        height: 600;
    }
    .links line {
      stroke: #999;
      stroke-opacity: 0.6;
    }

    .nodes circle {
      stroke: #fff;
      stroke-width: 1.5px;
    }
</style>

<script src="https://d3js.org/d3.v4.min.js"></script>
<div id="circlegraph"></div>

<script>

    var svg = d3.select("#circlegraph")
                .append("svg")
                    .attr("width", 960)
                    .attr("height", 600),
        width = +svg.attr("width"),
        height = +svg.attr("height");

    var color = d3.scaleOrdinal(d3.schemeCategory20);

    var chargeForce = d3.forceManyBody()
                        .strength(-3);

    var simulation = d3.forceSimulation()
        .force("link", d3.forceLink().id(function(d) { return d.id; }))
        .force("charge", chargeForce)
        .force("center", d3.forceCenter(width / 2, height / 2));

    d3.json("circleGraph.json", function(error, graph) {
      if (error) throw error;

      var link = svg.append("g")
          .attr("class", "links")
        .selectAll("line")
        .data(graph.links)
        .enter().append("line")
          .attr("stroke-width", function(d) { return Math.sqrt(d.value); });

      var node = svg.append("g")
          .attr("class", "nodes")
        .selectAll("circle")
        .data(graph.nodes)
        .enter().append("circle")
          .attr("r", 5)
          .attr("fill", function(d) { return color(d.group); })
          .call(d3.drag()
              .on("start", dragstarted)
              .on("drag", dragged)
              .on("end", dragended));

      node.append("title")
          .text(function(d) { return d.id; });

      simulation
          .nodes(graph.nodes)
          .on("tick", ticked);

      simulation.force("link")
          .links(graph.links);

      function ticked() {
        link
            .attr("x1", function(d) { return d.source.x; })
            .attr("y1", function(d) { return d.source.y; })
            .attr("x2", function(d) { return d.target.x; })
            .attr("y2", function(d) { return d.target.y; });

        node
            .attr("cx", function(d) { return d.x; })
            .attr("cy", function(d) { return d.y; });
      }
    });

    function dragstarted(d) {
      if (!d3.event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(d) {
      d.fx = d3.event.x;
      d.fy = d3.event.y;
    }

    function dragended(d) {
      if (!d3.event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }
</script>
""")
